$(".confirm-block").on("click", function (e) {
    e.preventDefault() // Prevent the default action

    const userId = $(this).data("id")
    const checkbox = $(this).find(".check")
    const isChecked = checkbox.prop("checked")

    Swal.fire({
        title: "Are you sure?",
        text: "This action will block/unblock the user!",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Yes, block/unblock it!",
        confirmButtonClass: "btn btn-primary",
        cancelButtonClass: "btn btn-danger ml-1",
        buttonsStyling: false,
    }).then(function (result) {
        if (result.isConfirmed) {
            // Perform the block/unblock action
            const blocked = isChecked // If checked, it means it will be blocked

            $.ajax({
                url: "/admin/updateUserStatus/" + userId,
                type: "PUT",
                data: JSON.stringify({ blocked: blocked }), // Send the blocked status
                contentType: "application/json", // Set content type to JSON
                success: function (response) {
                    Swal.fire({
                        icon: "success",
                        title: "User status updated successfully!",
                        confirmButtonClass: "btn btn-success",
                    }).then(() => {
                        // Toggle the checkbox state only if the update was successful
                        checkbox.prop("checked", !isChecked)
                        console.log("update successful");
                    })
                },
                error: function (error) {
                    console.log("Error:", error.responseText) // Log the error to the console
                    Swal.fire({
                        title: "Error",
                        text: "An error occurred while updating the user status.",
                        icon: "error",
                        confirmButtonClass: "btn btn-danger",
                    })
                },
            })
        } else if (result.dismiss === Swal.DismissReason.cancel) {
            Swal.fire({
                title: "Cancelled",
                text: "Your action has been cancelled.",
                icon: "error",
                confirmButtonClass: "btn btn-success",
            })
        }
    })
})
